    public void showLaporan(){
        DefaultTableModel dataModel =(DefaultTableModel) tabelLaporan.getModel();
        DecimalFormat kursIndonesia = (DecimalFormat) DecimalFormat.getCurrencyInstance();
        DecimalFormatSymbols formatRp = new DecimalFormatSymbols();
        formatRp.setCurrencySymbol("Rp. ");
        formatRp.setMonetaryDecimalSeparator(',');
        formatRp.setGroupingSeparator('.');
        kursIndonesia.setDecimalFormatSymbols(formatRp);
        int hargaJumlah=0;
        int jumlah;
        int lengthRow = dataModel.getRowCount();
        for (int n=0; n<lengthRow; n++) {
            dataModel.removeRow(0); 
        }
        try{
            Connection conn = (Connection) Server.serverData();
            Statement st = conn.createStatement();
            String sql = "SELECT id_laporan, id_menu, nama_menu, harga, total, hargatotal from laporan ORDER BY id_laporan ASC";
            ResultSet rs = st.executeQuery(sql);
            while (rs.next()){
                dataModel.addRow(new Object [] {
                    rs.getString(1),
                    rs.getString(2),
                    rs.getString(3),
                    rs.getString(4),
                    rs.getString(5),
                    rs.getString(6)
                });
            }
            int lengthR = dataModel.getRowCount();
            for (int i=0;i<lengthR;i++){
                jumlah = Integer.parseInt(dataModel.getValueAt(i, 5).toString());
                hargaJumlah = hargaJumlah + jumlah;
            }
            txtTotal.setText(kursIndonesia.format(hargaJumlah));
            
            rs.close();
            st.close();
        }catch (Exception e){
            JOptionPane.showMessageDialog(null, "Error : " + e);
        }
    }
    
    public void clearData(){
        try{
            Connection conn = (Connection) Server.serverData();
            Statement st = conn.createStatement();
            String sql = "TRUNCATE laporan";
            st.executeUpdate(sql);
            showLaporan();
            st.close();
        }catch(Exception e){
            JOptionPane.showMessageDialog(null, "Error : " + e);
        }
    }
    
    public void cetakData(){
        try{
            Connection conn = (Connection) Server.serverData();
            HashMap param = new HashMap();
            InputStream file = getClass().getResourceAsStream("/Data/cetak_laporan.jrxml");
            JasperDesign jasperDesign = JRXmlLoader.load(file);
            JasperReport jasperReport = JasperCompileManager.compileReport(jasperDesign);
            JasperPrint jasperPrint = JasperFillManager.fillReport(jasperReport, param, conn);
            JasperViewer.viewReport(jasperPrint, false);
            JasperViewer.setDefaultLookAndFeelDecorated(true);
        }catch (Exception e){
            JOptionPane.showMessageDialog(null, "Error : " + e);
        }
    }
    

