    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
        
        int confirm = JOptionPane.showConfirmDialog(null, "Apakah Anda Yakin Akan Menghapus Seluruh Data Ini ?", "KONFIRMASI", YES_NO_OPTION);
            if (confirm == JOptionPane.YES_OPTION)  {
                clearData();
                JOptionPane.showMessageDialog(null, "DATA BERHASIL DIRESET");
                showLaporan();
            }else{
                showLaporan();
                JOptionPane.showMessageDialog(null, "DATA TIDAK JADI DIRESET");
            }
    }//GEN-LAST:event_jButton2ActionPerformed

